import React, { useState } from 'react';

const Form = () => {
  const initialValues = {
    nombre: '',
    email: '',
  };

  const [values, setValues] = useState(initialValues);
  const [errors, setErrors] = useState({});
  const [submitted, setSubmitted] = useState(false);
  const [message, setMessage] = useState('');

  const enviarForm = (event) => {
    event.preventDefault();

    const errors = validate(values);
    setErrors(errors);

    if (Object.keys(errors).length === 0) {
      setMessage(`<h3>Gracias ${values.nombre}, te contactaremos cuanto antes vía email</h3>`);
      setValues(initialValues);
      setSubmitted(true);
    }
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setValues({ ...values, [name]: value });
  };

  const validate = (values) => {
    const errors = {};
    const nameRegex = /^[a-zA-Z ]+$/;
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;

    if (!values.nombre.trim()) {
      errors.nombre = 'Debe ingresar un nombre válido';
    } else if (!nameRegex.test(values.nombre)) {
      errors.nombre = 'El nombre solo puede contener letras y espacios';
    }

    if (!values.email.trim()) {
      errors.email = 'Debe ingresar un email válido';
    } else if (!emailRegex.test(values.email)) {
      errors.email = 'Debe ingresar un email válido';
    }

    return errors;
  };

  return (
    <div className='form-container'>
      <h1 className='form-title'>Formulario de contacto</h1>
      <form className='form' onSubmit={enviarForm}>
        <div className='form-control'>
          <label htmlFor='nombre'>Nombre completo</label>
          <input type='text' id='nombre' name='nombre' value={values.nombre} onChange={handleChange} />
          {errors.nombre && <p className='error'>{errors.nombre}</p>}
        </div>
        <div className='form-control'>
          <label htmlFor='email'>Email</label>
          <input type='email' id='email' name='email' value={values.email} onChange={handleChange} />
          {errors.email && <p className='error'>{errors.email}</p>}
        </div>
        <button type='submit' className='btn'>
          Enviar
        </button>
      </form>
      {message && <div className='message' dangerouslySetInnerHTML={{ __html: message }}></div>}

    </div>
  );
};

export default Form;
