import React, { useState } from 'react';
import { Button, Grid, TextField, Typography } from '@mui/material';

const Form = () => {
  const [nombre, setNombre] = useState('');
  const [email, setEmail] = useState('');
  const [nombreError, setNombreError] = useState('');
  const [emailError, setEmailError] = useState('');

  const handleNombreChange = (event) => {
    setNombre(event.target.value);
    setNombreError('');
  };

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
    setEmailError('');
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    let isValid = true;

    if (nombre.length < 5) {
      setNombreError('El nombre debe tener más de 5 letras');
      isValid = false;
    }

    if (!/\S+@\S+\.\S+/.test(email)) {
      setEmailError('Debe ingresar un email válido');
      isValid = false;
    }

    if (isValid) {
      console.log({ nombre, email });
      document.querySelector('.mensaje').innerHTML = `<h3>Gracias ${nombre}, te contactaremos cuanto antes vía email</h3>`;
      setNombre('');
      setEmail('');
    }
  };

  return (
    <div className='form-container'>
      <Typography sx={{ marginBottom: '50px' }} color='primary' variant='h4' align='center'>
        Formulario de contacto
      </Typography>
      <form action='' className='form-container' onSubmit={handleSubmit}>
        <Grid container direction='row' alignItems={'center'} justifyContent='space-evenly' spacing={2} sx={{ width: '100%' }}>
          <Grid item xs={12} md={11}>
            <TextField
              type='text'
              name='nombre'
              label='Nombre completo'
              value={nombre}
              onChange={handleNombreChange}
              error={!!nombreError}
              helperText={nombreError}
              variant='outlined'
              fullWidth
              color='secondary'
            />
          </Grid>
          <Grid item xs={12} md={11}>
            <TextField
              type='email'
              name='email'
              label='Email'
              value={email}
              onChange={handleEmailChange}
              error={!!emailError}
              helperText={emailError}
              variant='outlined'
              fullWidth
              color='secondary'
            />
          </Grid>
        </Grid>
        <Button type='submit' variant='contained'>
          Enviar
        </Button>
      </form>
      <div className='mensaje'></div>
    </div>
  );
};

export default Form;
