import React, { useContext, useEffect } from 'react';
import Card from '../Components/Card'
import { ContextGlobal } from "../Components/utils/global.context";


//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Home = () => {
  const { globalState, dispatch } = useContext(ContextGlobal);
  const { theme, data } = globalState;

  // const [dentist,setDentists] = useState([])

  // const getDentists = async() => {
  //   const data = await fetch('https://jsonplaceholder.typicode.com/users')
  //   .then((response) => {
  //     console.log("Respuesta", response)
  //     return response.json()
  //   });
  //   setDentists(data)
  // }

  useEffect(() => {
    if (data.length === 0) {
      // Si no hay data en el contexto, hacemos la petición a la API
      fetch("https://jsonplaceholder.typicode.com/users")
        .then((response) => response.json())
        .then((data) => {
          dispatch({ type: "SET_DATA", payload: data });
        })
        .catch((error) => console.error(error));
    }
  }, [data]);

  return (
    <main className={`theme-${theme}`}>
      <h1>Home</h1>
      <div className='card-grid'>
        {data.map((dentist) => (
          <Card key={dentist.id} name={dentist.name} id={dentist.id} username={dentist.username} />
        ))}
      </div>
    </main>
  )
}

export default Home
