      eliminarDeFavorito(id, name)
    }
  }

  return (
    <div className="card">
      <img src="images/doctor.jpg" alt="foto_dentista" />
      <Link to={`/dentist/${id}`} data={state.data}>{name}</Link>
      <p>{username}</p>
      <button onClick={addFav} className="favButton" >{esFavorito(id) ? "üíó" : "ü§ç"}</button>
    </div>
  );
};

