import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { ContextGlobal } from "../Components/utils/global.context";
import { setFavoritosStorage, eliminarDeFavorito, esFavorito} from "../Components/utils/metodosLocalStorage";

const Card = ({ name, username, id }) => {
  const { state, dispatch } = useContext(ContextGlobal);
  const addFav = ()=>{
    // Aqui iria la logica para agregar la Card en el localStorage

    if (esFavorito(id)) {
      eliminarDeFavorito(id, name);
      dispatch({ 
        type: "FAV", 
        payload: state.favoritos.filter(fav => fav.id !== id) });
    } else {
      const odontologoFav = { name, username, id };
      setFavoritosStorage(odontologoFav);
      dispatch({ type: "FAV", payload: [...state.favoritos, odontologoFav] });

    }
  }

  return (
    <Link to = {`detail/${id}`}>
    <div className="card">
        {/* En cada card deberan mostrar en name - username y el id */}

        {/* No debes olvidar que la Card a su vez servira como Link hacia la pagina de detalle */}

        {/* Ademas deberan integrar la logica para guardar cada Card en el localStorage */}
        <img src="images/doctor.jpg" alt='DH-logo' width="100em" />
        <h1>
          {name}
        </h1>
        <h2>
          {username}
        </h2>
        <h3>
          {id}
        </h3>
        <button onClick={addFav} className="favButton" >{esFavorito(id) ? "⭐️" : "✩"}</button>

    </div>
    </Link>
  );
};

export default Card;
