import React, { useState } from "react";

const Form = () => {
  const [nombre, setNombre] = useState("");
  const [email, setEmail] = useState("");
  const [nombreError, setNombreError] = useState("");
  const [emailError, setEmailError] = useState("");
  const [submitted, setSubmitted] = useState(false);

  const handleNombreChange = (e) => {
    setNombre(e.target.value);
  };

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const isValid = validate();
    if (isValid) {
      const data = {
        nombre,
        email
      };
      console.log(data);
      setSubmitted(true);
    }
  };

  const validate = () => {
    let nombreError = "";
    let emailError = "";

    if (!nombre || nombre.length < 5) {
      nombreError = "El nombre debe tener más de 5 letras";
    }

    if (!email || !/^\S+@\S+\.\S+$/.test(email)) {
      emailError = "Debe ingresar un email válido";
    }

    setNombreError(nombreError);
    setEmailError(emailError);

    return !nombreError && !emailError;
  };

  return (
    <div className="form-container">
      <h4 style={{ marginBottom: "50px", color: "blue" }}>Formulario de contacto</h4>
      {!submitted ? (
        <form onSubmit={handleSubmit}>
          <div>
            <label htmlFor="nombre">Nombre completo:</label>
            <input
              type="text"
              id="nombre"
              name="nombre"
              value={nombre}
              onChange={handleNombreChange}
            />
            {nombreError && <div className="error">{nombreError}</div>}
          </div>
          <div>
            <label htmlFor="email">Email:</label>
            <input
              type="email"
              id="email"
              name="email"
              value={email}
              onChange={handleEmailChange}
            />
            {emailError && <div className="error">{emailError}</div>}
          </div>
          <button type="submit">Enviar</button>
        </form>
      ) : (
        <div className="mensaje">
          <h3>
            Gracias {nombre}, te contactaremos cuanto antes vía email
          </h3>
        </div>
      )}
    </div>
  );
};

export default Form;
